CREATE TABLE EMPLOYEE (ID INTEGER AUTO_INCREMENT NOT NULL, EMPNAME VARCHAR(255), DEPARTMENT_ID INTEGER, PRIMARY KEY (ID))
CREATE TABLE DEPARTMENT (ID INTEGER AUTO_INCREMENT NOT NULL, DEPTNAME VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE DEPARTMENT_EMPLOYEE (Department_ID INTEGER NOT NULL, employees_ID INTEGER NOT NULL, PRIMARY KEY (Department_ID, employees_ID))
ALTER TABLE EMPLOYEE ADD CONSTRAINT FK_EMPLOYEE_DEPARTMENT_ID FOREIGN KEY (DEPARTMENT_ID) REFERENCES DEPARTMENT (ID)
ALTER TABLE DEPARTMENT_EMPLOYEE ADD CONSTRAINT FK_DEPARTMENT_EMPLOYEE_employees_ID FOREIGN KEY (employees_ID) REFERENCES EMPLOYEE (ID)
ALTER TABLE DEPARTMENT_EMPLOYEE ADD CONSTRAINT FK_DEPARTMENT_EMPLOYEE_Department_ID FOREIGN KEY (Department_ID) REFERENCES DEPARTMENT (ID)
